function r(e,t){this.firstName=e,this.lastName=t,this.grades=[]}r.prototype.inputNewGrade=function(e){this.grades.push(e)};r.prototype.computeAverageGrade=function(){return this.grades.length===0?0:this.grades.reduce((e,t)=>e+t,0)/this.grades.length};let a=new r("Dylan","Song");a.inputNewGrade(85);a.inputNewGrade(95);let n=new r("Eli","Smith");n.inputNewGrade(70);n.inputNewGrade(80);let i=[a,n],s=i.reduce((e,t)=>e+t.computeAverageGrade(),0)/i.length;console.log("Average grade of all:",s);
