function n(e,o){this.name=e,this.speed=o}n.prototype.run=function(e){this.speed+=e,console.log(`${this.name} is running at speed ${this.speed}`)};n.compareBySpeed=function(e,o){return e.speed-o.speed};function t(e,o){n.call(this,e,o)}t.prototype=Object.create(n.prototype);t.prototype.constructor=t;t.prototype.hide=function(){console.log(`${this.name} hides`)};let i=new t("Whitey",10);i.run(5);i.hide();let p=new t("Brownie",8);console.log(n.compareBySpeed(i,p));
