let i=[];function d(){return i}function o(t){return i.some(e=>e.id===t.id)?!1:(i.push(t),!0)}function r(t,e){const n=i.find(l=>l.id===t);return n?(n.title=e,!0):!1}function u(t){const e=i.findIndex(n=>n.id===t);return e===-1?!1:(i.splice(e,1),!0)}function s(t){const e=i.find(n=>n.id===t);return e?e.title:null}console.log(o({id:1,title:"Book One"}));console.log(o({id:2,title:"Book Two"}));console.log(d());console.log(r(1,"Updated Book One"));console.log(u(2));console.log(s(1));
